export declare const schema = "-- Create roles table\nCREATE TABLE public.roles (\n    id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n    name VARCHAR(255) NOT NULL UNIQUE,\n    description TEXT,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL\n);\n\n-- Create permissions table\nCREATE TABLE public.permissions (\n    id UUID DEFAULT gen_random_uuid() PRIMARY KEY,\n    name VARCHAR(255) NOT NULL UNIQUE,\n    description TEXT,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL\n);\n\n-- Create role_permissions junction table\nCREATE TABLE public.role_permissions (\n    role_id UUID REFERENCES public.roles(id) ON DELETE CASCADE,\n    permission_id UUID REFERENCES public.permissions(id) ON DELETE CASCADE,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL,\n    PRIMARY KEY (role_id, permission_id)\n);\n\n-- Create users table\nCREATE TABLE public.users (\n    id TEXT PRIMARY KEY,\n    email TEXT UNIQUE NOT NULL,\n    role_id UUID REFERENCES public.roles(id),\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL\n);\n\n-- Insert default roles\nINSERT INTO public.roles (name, description) VALUES\n    ('admin', 'Administrator role with full access'),\n    ('user', 'Standard user role');\n\n-- Insert default permissions\nINSERT INTO public.permissions (name, description) VALUES\n    ('create:posts', 'Can create new posts'),\n    ('edit:posts', 'Can edit existing posts'),\n    ('delete:posts', 'Can delete posts'),\n    ('manage:users', 'Can manage users'),\n    ('manage:roles', 'Can manage roles');\n\n-- Assign permissions to admin role\nINSERT INTO public.role_permissions (role_id, permission_id)\nSELECT r.id, p.id\nFROM public.roles r\nCROSS JOIN public.permissions p\nWHERE r.name = 'admin';\n\n-- Assign basic permissions to user role\nINSERT INTO public.role_permissions (role_id, permission_id)\nSELECT r.id, p.id\nFROM public.roles r\nCROSS JOIN public.permissions p\nWHERE r.name = 'user'\nAND p.name IN ('create:posts', 'edit:posts');";
